mapper.select:
SELECT ID, MEMBER_EMAIL, MEMBER_PASSWORD, MEMBER_NICKNAME, MEMBER_TELL, MEMBER_BIRTH, MEMBER_GENDER, MEMBER_POINT, MEMBER_IS_ACT, CREATED_DATE, UPDATED_DATE FROM (SELECT ROWNUM R, ID, MEMBER_EMAIL, MEMBER_PASSWORD, MEMBER_NICKNAME, MEMBER_TELL, MEMBER_BIRTH, MEMBER_GENDER, MEMBER_POINT, MEMBER_IS_ACT, CREATED_DATE, UPDATED_DATE FROM (SELECT ID, MEMBER_EMAIL, MEMBER_PASSWORD, MEMBER_NICKNAME, MEMBER_TELL, MEMBER_BIRTH, MEMBER_GENDER, MEMBER_POINT, MEMBER_IS_ACT, CREATED_DATE, UPDATED_DATE FROM TBL_MEMBER
ORDER BY ID DESC ) B
WHERE ROWNUM <= ${pagination.endRow} ) WHERE R >= #{pagination.startRow}

SELECT * FROM TBL_MEMBER
WHERE MEMBER_EMAIL = #{email}
AND MEMBER_PASSWORD IS NULL

mapper.update:
UPDATE TBL_MEMBER
SET MEMBER_NICKNAME = #{memberNickname}, MEMBER_BIRTH = #{memberBirth}, MEMBER_GENDER = #{memberGender}, UPDATED_DATE = SYSDATE
WHERE ID = #{id}

mapper.select:
SELECT MEMBER_EMAIL, MEMBER_PASSWORD
FROM TBL_MEMBER
WHERE MEMBER_EMAIL = #{memberEmail}

SELECT MEMBER_PASSWORD
FROM TBL_MEMBER
WHERE ID = #{id}

mapper.update:
UPDATE TBL_MEMBER
SET MEMBER_PASSWORD = #{memberPassword}
WHERE ID = #{id}
