mapper.select:
SELECT ID, NOTICE_NAME, NOTICE_CONTENT, ADMIN_ID, ADMIN_NAME, CREATED_DATE, UPDATED_DATE FROM (SELECT ROWNUM R, ID, NOTICE_NAME, NOTICE_CONTENT, ADMIN_ID, ADMIN_NAME, CREATED_DATE, UPDATED_DATE FROM (SELECT N.ID, N.NOTICE_NAME, N.NOTICE_CONTENT, N.ADMIN_ID, A.ADMIN_NAME, N.CREATED_DATE, N.UPDATED_DATE FROM TBL_ADMIN A JOIN TBL_NOTICE N ON A.ID = N.ADMIN_ID
ORDER BY A.ID DESC ) B
WHERE ROWNUM <= ${criteria.endRow} ) WHERE R >= #{criteria.startRow}
