mapper.select:
SELECT ID, MEMBER_NICKNAME, PLAN_NAME, PLAN_START_DATE, 'MM/DD', PLAN_END_DATE, 'MM/DD', PLAN_DEADLINE, PLAN_MAX_PERSONNEL, PLAN_MIN_PERSONNEL, PLAN_PRICE, MEMBER_ID, COURSE_ID FROM ( SELECT ROWNUM R , ID, MEMBER_NICKNAME, PLAN_NAME, PLAN_START_DATE, PLAN_END_DATE, PLAN_DEADLINE, PLAN_MAX_PERSONNEL, PLAN_MIN_PERSONNEL, PLAN_PRICE, MEMBER_ID, COURSE_ID FROM ( SELECT M.MEMBER_NICKNAME, P.ID, P.PLAN_NAME, P.PLAN_START_DATE, P.PLAN_END_DATE, P.PLAN_DEADLINE, P.PLAN_MAX_PERSONNEL, P.PLAN_MIN_PERSONNEL, P.PLAN_PRICE, P.MEMBER_ID, P.COURSE_ID FROM TBL_MEMBER M JOIN TBL_PLAN P ON M.ID = P.MEMBER_ID WHERE MEMBER_ID = 1 ORDER BY P.ID DESC )
WHERE ROWNUM <= ${pagination.endRow}
) WHERE R >= ${pagination.startRow}
